// Extract client name from filename
const filePath = $input.first().json.path;
const fileName = filePath.split('/').pop();
const clientName = fileName.replace(': Estate Planning Advisor Meeting.txt', '').trim();

return {
  json: {
    client_name: clientName,
    file_path: filePath,
    transcript_content: $input.first().binary.data.data.toString('utf8')
  }
};

// Estate Planning Knowledge Graph Analysis (Local)
const content = $input.first().json.transcript_content.toLowerCase();

// Enhanced domain knowledge patterns
const knowledgeBase = {
  family_structures: {
    blended_family: {
      patterns: ['stepchildren', 'previous marriage', 'ex-wife', 'ex-husband', 'half-siblings'],
      weight: 1.5,
      planning_impact: 'high'
    },
    special_needs: {
      patterns: ['special needs', 'disabled child', 'disability benefits', 'SSI', 'medicaid'],
      weight: 2.0,
      planning_impact: 'critical'
    },
    minor_children: {
      patterns: ['young children', 'minor children', 'under 18', 'guardianship'],
      weight: 1.2,
      planning_impact: 'medium'
    }
  },
  business_structures: {
    LLC: {
      patterns: ['LLC', 'limited liability company', 'operating agreement', 'member interests'],
      complexity_score: 6
    },
    S_Corp: {
      patterns: ['S corp', 'S corporation', 'shareholder agreement', 'pass-through'],
      complexity_score: 7
    }
  },
  urgency_factors: {
    high: {
      patterns: ['health issues', 'business sale', 'estate tax'],
      score: 7
    },
    medium: {
      patterns: ['aging', 'retirement planning', 'tax planning'],
      score: 5
    }
  }
};

// Calculate relevance scores
function calculateRelevance(text, categoryData) {
  const results = {};

  for (const [subcat, config] of Object.entries(categoryData)) {
    let score = 0;

    for (const pattern of config.patterns) {
      if (text.includes(pattern)) {
        score += 1;
      }
    }

    const weight = config.weight || 1;
    results[subcat] = {
      score: (score / config.patterns.length) * weight,
      metadata: config
    };
  }

  return results;
}

// Analyze context
const analysis = {
  family: calculateRelevance(content, knowledgeBase.family_structures),
  business: calculateRelevance(content, knowledgeBase.business_structures),
  urgency: calculateRelevance(content, knowledgeBase.urgency_factors)
};

// Get dominant patterns
function getDominant(categoryAnalysis) {
  const entries = Object.entries(categoryAnalysis);
  if (entries.length === 0) return ['unknown', 0];

  const dominant = entries.reduce((a, b) => a[1].score > b[1].score ? a : b);
  return [dominant[0], dominant[1].score];
}

const dominantFamily = getDominant(analysis.family);
const dominantBusiness = getDominant(analysis.business);
const dominantUrgency = getDominant(analysis.urgency);

return {
  json: {
    ...($input.first().json),
    context_analysis: analysis,
    dominant_patterns: {
      family_type: dominantFamily[0],
      family_confidence: dominantFamily[1],
      business_type: dominantBusiness[0],
      business_confidence: dominantBusiness[1],
      urgency_level: dominantUrgency[0],
      urgency_confidence: dominantUrgency[1]
    }
  }
};

