// Parse and validate Mistral response
const data = $input.first().json;
const mistralResponse = $('Local Mistral Extraction').first().json.response;

// Extract JSON from response
let extractedData;
try {
  const jsonMatch = mistralResponse.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    extractedData = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('No JSON found');
  }
} catch (error) {
  // Fallback data
  extractedData = {
    client_name: data.client_name,
    state: null,
    marital_status: 'Unknown',
    children_count: 0,
    estate_value: 0,
    meeting_stage: 'Follow Up',
    urgency_score: 5,
    pain_points: 'Estate planning consultation',
    objections: ''
  };
}

// Calculate quality score
let qualityScore = 50; // Base score

// Context validation bonuses
if (data.dominant_patterns.family_confidence > 0.3) qualityScore += 15;
if (data.dominant_patterns.business_confidence > 0.3) qualityScore += 15;
if (data.dominant_patterns.urgency_confidence > 0.3) qualityScore += 10;
if (extractedData.state && extractedData.state !== 'null') qualityScore += 10;

return {
  json: {
    extracted_data: extractedData,
    quality_score: Math.min(100, qualityScore),
    context_enhanced: true,
    original_data: data
  }
};

// Format for Baserow CRM
const data = $input.first().json;
const extracted = data.extracted_data;

// Baserow field mappings
const maritalStatusMap = {
  'Single': 2988,
  'Married': 2989,
  'Widowed': 2990,
  'Divorced': 2991
};

const meetingStageMap = {
  'Closed Won': 2984,
  'Closed Lost': 2985,
  'Follow Up': 2987
};

const baserowPayload = {
  'field_6755': extracted.client_name,
  'field_6756': meetingStageMap[extracted.meeting_stage] || 2987,
  'field_6757': maritalStatusMap[extracted.marital_status] || null,
  'field_6758': parseInt(extracted.children_count) || 0,
  'field_6759': parseInt(extracted.estate_value) || 0,
  'field_6760': 1,
  'field_6761': 0,
  'field_6762': extracted.pain_points || 'Estate planning consultation',
  'field_6763': extracted.objections || '',
  'field_6764': parseInt(extracted.urgency_score) || 5,
  'field_6765': extracted.meeting_stage !== 'Closed Won',
  'field_6766': `${extracted.client_name}: Estate Planning Advisor Meeting.txt`,
  'field_6767': new Date().toISOString().split('T')[0]
};

return {
  json: {
    baserow_payload: baserowPayload,
    quality_metadata: {
      quality_score: data.quality_score,
      context_enhanced: data.context_enhanced,
      auto_approved: data.quality_score >= 70
    }
  }
};
