// Extract client name from filename
const filePath = $input.first().json.path;
const fileName = filePath.split('/').pop();
const clientName = fileName.replace(': Estate Planning Advisor Meeting.txt', '').trim();

return {
  json: {
    client_name: clientName,
    file_path: filePath,
    transcript_content: $input.first().binary.data.data.toString('utf8')
  }
};

// Estate Planning Knowledge Graph Analysis (Local)
const content = $input.first().json.transcript_content.toLowerCase();

// Enhanced domain knowledge patterns
const knowledgeBase = {
  family_structures: {
    blended_family: {
      patterns: ['stepchildren', 'previous marriage', 'ex-wife', 'ex-husband', 'half-siblings', 'step-daughter', 'step-son', 'second marriage'],
      weight: 1.5, // Higher complexity multiplier
      planning_impact: 'high'
    },
    special_needs: {
      patterns: ['special needs', 'disabled child', 'disability benefits', 'SSI', 'medicaid', 'special needs trust', 'autism', 'down syndrome'],
      weight: 2.0,
      planning_impact: 'critical'
    },
    minor_children: {
      patterns: ['young children', 'minor children', 'under 18', 'guardianship', 'custody', 'college fund'],
      weight: 1.2,
      planning_impact: 'medium'
    },
    elderly_parents: {
      patterns: ['aging parents', 'elderly mother', 'elderly father', 'nursing home', 'long-term care'],
      weight: 1.3,
      planning_impact: 'medium'
    }
  },
  business_structures: {
    LLC: {
      patterns: ['LLC', 'limited liability company', 'operating agreement', 'member interests', 'membership units'],
      complexity_score: 6,
      tax_implications: 'pass-through'
    },
    S_Corp: {
      patterns: ['S corp', 'S corporation', 'S election', 'shareholder agreement', 'pass-through taxation'],
      complexity_score: 7,
      tax_implications: 'pass-through'
    },
    C_Corp: {
      patterns: ['C corp', 'C corporation', 'double taxation', 'corporate minutes', 'board of directors'],
      complexity_score: 8,
      tax_implications: 'double-tax'
    },
    partnership: {
      patterns: ['partnership', 'general partner', 'limited partner', 'partnership agreement', 'K-1'],
      complexity_score: 7,
      tax_implications: 'pass-through'
    }
  },
  urgency_factors: {
    critical: {
      patterns: ['terminal illness', 'immediate surgery', 'health crisis', 'emergency', 'urgent medical'],
      score: 9,
      timeframe: 'immediate'
    },
    high: {
      patterns: ['health issues', 'recent diagnosis', 'business sale', 'significant assets', 'estate tax'],
      score: 7,
      timeframe: 'weeks'
    },
    medium: {
      patterns: ['aging', 'retirement planning', 'wealth transfer', 'tax planning', 'business succession'],
      score: 5,
      timeframe: 'months'
    },
    low: {
      patterns: ['young family', 'informational', 'future planning', 'education', 'general questions'],
      score: 3,
      timeframe: 'years'
    }
  },
  meeting_outcomes: {
    closed_won: {
      patterns: ['payment processed', 'already charged', 'building documents', 'moving forward', 'ready to start', 'pull the trigger', 'visa', 'mastercard', 'american express'],
      confidence_boost: 1.5
    },
    follow_up: {
      patterns: ['think about it', 'discuss with spouse', 'call back', 'need time', 'follow up', 'reach out', 'next week', 'consider options'],
      confidence_boost: 1.2
    },
    closed_lost: {
      patterns: ['not interested', 'too expensive', 'can\'t afford', 'not ready', 'maybe later', 'no thank you'],
      confidence_boost: 1.3
    }
  },
  location_indicators: {
    question_patterns: ['where in the world', 'where are you joining from', 'where are you calling from', 'what state', 'where are you located'],
    response_patterns: ['I\'m in', 'I live in', 'calling from', 'located in', 'we\'re in', 'based in', 'from']
  }
};

// Enhanced relevance calculation with context weighting
function calculateEnhancedRelevance(text, categoryData) {
  const results = {};

  for (const [subcat, config] of Object.entries(categoryData)) {
    let score = 0;
    let patternMatches = [];

    for (const pattern of config.patterns) {
      const regex = new RegExp(`\\b${pattern.replace(/[.*+?^${}()|[\]\\]/g, '\\$&')}\\b`, 'gi');
      const matches = text.match(regex);
      if (matches) {
        score += matches.length;
        patternMatches.push(...matches);
      }
    }

    // Apply weighting and normalization
    const weight = config.weight || config.confidence_boost || 1;
    const normalizedScore = (score / config.patterns.length) * weight;

    results[subcat] = {
      score: normalizedScore,
      raw_matches: score,
      patterns_found: patternMatches,
      metadata: {
        complexity_score: config.complexity_score,
        planning_impact: config.planning_impact,
        urgency_score: config.score,
        timeframe: config.timeframe
      }
    };
  }

  return results;
}

// Analyze all categories with enhanced scoring
const contextAnalysis = {
  family_complexity: calculateEnhancedRelevance(content, knowledgeBase.family_structures),
  business_complexity: calculateEnhancedRelevance(content, knowledgeBase.business_structures),
  urgency_analysis: calculateEnhancedRelevance(content, knowledgeBase.urgency_factors),
  outcome_analysis: calculateEnhancedRelevance(content, knowledgeBase.meeting_outcomes)
};

// Determine dominant patterns with confidence
function getDominantPattern(analysis) {
  const entries = Object.entries(analysis);
  if (entries.length === 0) return ['unknown', 0, {}];

  const dominant = entries.reduce((a, b) => a[1].score > b[1].score ? a : b);
  return [dominant[0], dominant[1].score, dominant[1].metadata || {}];
}

const dominantFamily = getDominantPattern(contextAnalysis.family_complexity);
const dominantBusiness = getDominantPattern(contextAnalysis.business_complexity);
const dominantUrgency = getDominantPattern(contextAnalysis.urgency_analysis);
const dominantOutcome = getDominantPattern(contextAnalysis.outcome_analysis);

// Calculate overall complexity score
const complexityScore = (
  (dominantFamily[1] * 0.3) +
  (dominantBusiness[1] * 0.3) +
  (dominantUrgency[1] * 0.4)
) * 10; // Scale to 0-10

return {
  json: {
    ...($input.first().json),
    context_analysis: {
      family_analysis: contextAnalysis.family_complexity,
      business_analysis: contextAnalysis.business_complexity,
      urgency_analysis: contextAnalysis.urgency_analysis,
      outcome_analysis: contextAnalysis.outcome_analysis,
      overall_complexity: complexityScore
    },
    dominant_patterns: {
      family_type: dominantFamily[0],
      family_confidence: dominantFamily[1],
      family_metadata: dominantFamily[2],
      business_type: dominantBusiness[0],
      business_confidence: dominantBusiness[1],
      business_metadata: dominantBusiness[2],
      urgency_level: dominantUrgency[0],
      urgency_confidence: dominantUrgency[1],
      urgency_metadata: dominantUrgency[2],
      meeting_outcome: dominantOutcome[0],
      outcome_confidence: dominantOutcome[1],
      complexity_score: complexityScore
    },
    processing_metadata: {
      analysis_timestamp: new Date().toISOString(),
      knowledge_base_version: '2.0',
      local_processing: true
    }
  }
};

