// Create enhanced prompt with local context and similar cases
const data = $input.first().json;
const similarCases = $('Vector Similarity Search').first()?.json?.result || [];

// Build context from similar cases
let similarCasesContext = '';
if (similarCases.length > 0) {
  similarCasesContext = `\n\nüîç SIMILAR CASES FOR REFERENCE (${similarCases.length} found):\n`;
  similarCases.forEach((case_, idx) => {
    const payload = case_.payload;
    const similarity = (case_.score * 100).toFixed(1);
    similarCasesContext += `${idx + 1}. ${payload.client_name} (${similarity}% similar):\n`;
    similarCasesContext += `   - Family: ${payload.family_context} | Business: ${payload.business_context}\n`;
    similarCasesContext += `   - Urgency: ${payload.urgency_context} | Outcome: ${payload.meeting_outcome}\n`;
    similarCasesContext += `   - Complexity: ${payload.complexity_score}/10\n\n`;
  });
}

// Enhanced domain-specific prompt
const enhancedPrompt = `You are a Senior Estate Planning Attorney with 25+ years of experience analyzing client consultation transcripts.

üéØ CLIENT ANALYSIS FOR ${data.client_name}:

üìä CONTEXT ANALYSIS (Knowledge Graph):
- Family Structure: ${data.dominant_patterns.family_type} (confidence: ${(data.dominant_patterns.family_confidence * 100).toFixed(1)}%)
  ‚îî‚îÄ Impact: ${data.dominant_patterns.family_metadata.planning_impact || 'standard'}

- Business Complexity: ${data.dominant_patterns.business_type} (confidence: ${(data.dominant_patterns.business_confidence * 100).toFixed(1)}%)
  ‚îî‚îÄ Score: ${data.dominant_patterns.business_metadata.complexity_score || 'N/A'}/10

- Urgency Level: ${data.dominant_patterns.urgency_level} (confidence: ${(data.dominant_patterns.urgency_confidence * 100).toFixed(1)}%)
  ‚îî‚îÄ Score: ${data.dominant_patterns.urgency_metadata.urgency_score || 'N/A'}/10
  ‚îî‚îÄ Timeframe: ${data.dominant_patterns.urgency_metadata.timeframe || 'standard'}

- Predicted Outcome: ${data.dominant_patterns.meeting_outcome} (confidence: ${(data.dominant_patterns.outcome_confidence * 100).toFixed(1)}%)

üß† OVERALL COMPLEXITY: ${data.dominant_patterns.complexity_score.toFixed(1)}/10
${similarCasesContext}

üìã EXTRACTION INSTRUCTIONS:
Based on the context analysis above and similar case patterns, extract with MAXIMUM ACCURACY:

üìÑ TRANSCRIPT TO ANALYZE:
${data.transcript_content.substring(0, 7000)}

üéØ EXTRACT EXACTLY (JSON FORMAT):
{
  "client_name": "${data.client_name}",
  "state": "Full state name where client is located (CRITICAL: Look for location question/answer pattern)",
  "marital_status": "Single/Married/Divorced/Widowed (look for spouse/wife/husband mentions)",
  "children_count": "Exact number of children (count sons, daughters, kids mentions)",
  "estate_value": "Total estate value in dollars (look for asset discussions, property values)",
  "business_entities": "Number of business interests based on context analysis: ${data.dominant_patterns.business_type}",
  "meeting_stage": "Closed Won/Follow Up/Closed Lost (based on final conversation outcome)",
  "urgency_score": "1-10 rating (use context analysis: ${data.dominant_patterns.urgency_level})",
  "pain_points": "Primary client concerns and motivations for estate planning",
  "objections": "Any resistance, concerns, or hesitations expressed",
  "context_validation": {
    "family_structure_match": "Does extraction confirm ${data.dominant_patterns.family_type} indicators?",
    "business_structure_match": "Does extraction confirm ${data.dominant_patterns.business_type} indicators?",
    "urgency_level_match": "Does extraction confirm ${data.dominant_patterns.urgency_level} indicators?",
    "outcome_prediction_match": "Does extraction confirm ${data.dominant_patterns.meeting_outcome} prediction?"
  },
  "confidence_scores": {
    "overall": "1-10 overall extraction confidence",
    "state": "1-10 (look for \"where are you joining from\" question pattern)",
    "marital_status": "1-10 (look for spouse/family references)",
    "children_count": "1-10 (count specific mentions)",
    "estate_value": "1-10 (look for dollar amounts, asset discussions)",
    "meeting_stage": "1-10 (analyze final conversation outcome)"
  },
  "extraction_notes": "Brief notes on extraction reasoning and any uncertainties"
}

‚ö†Ô∏è  CRITICAL REQUIREMENTS:
1. Use knowledge graph context to guide extraction
2. Cross-reference similar cases for accuracy
3. Pay special attention to fields with low historical accuracy
4. Return ONLY valid JSON
5. Be conservative with confidence scores
6. Use context analysis to validate extractions`;

return {
  json: {
    ...data,
    enhanced_prompt: enhancedPrompt,
    similar_cases_count: similarCases.length,
    prompt_metadata: {
      prompt_version: '3.0_local',
      context_enhanced: true,
      similarity_threshold: 0.7,
      knowledge_graph_applied: true
    }
  }
};

// Enhanced validation against context analysis
const data = $input.first().json;
const mistralResponse = $('Local Mistral-7B Extraction').first().json.response;

// Extract JSON from Mistral response
let extractedData;
try {
  // Look for JSON in the response
  const jsonMatch = mistralResponse.match(/\{[\s\S]*\}/g);
  if (jsonMatch && jsonMatch.length > 0) {
    // Take the largest JSON object found
    const largestJson = jsonMatch.reduce((a, b) => a.length > b.length ? a : b);
    extractedData = JSON.parse(largestJson);
  } else {
    throw new Error('No valid JSON found in Mistral response');
  }
} catch (error) {
  console.log('‚ùå Extraction failed, creating enhanced fallback:', error.message);

  // Enhanced fallback using context analysis
  extractedData = {
    client_name: data.client_name,
    state: null,
    marital_status: 'Unknown',
    children_count: 0,
    estate_value: 0,
    business_entities: data.dominant_patterns.business_type !== 'none' ? 1 : 0,
    meeting_stage: data.dominant_patterns.meeting_outcome === 'closed_won' ? 'Closed Won' : 'Follow Up',
    urgency_score: data.dominant_patterns.urgency_metadata.urgency_score || 5,
    pain_points: 'Estate planning consultation - extraction failed',
    objections: '',
    context_validation: {
      family_structure_match: 'Unable to validate - extraction failed',
      business_structure_match: 'Partial - used context analysis',
      urgency_level_match: 'Partial - used context analysis',
      outcome_prediction_match: 'Partial - used context analysis'
    },
    confidence_scores: {
      overall: 2,
      state: 1,
      marital_status: 1,
      children_count: 1,
      estate_value: 1,
      meeting_stage: 6
    },
    extraction_notes: 'Fallback data used due to extraction failure'
  };
}

// Enhanced validation with context cross-referencing
const validation = {
  context_match_score: 0,
  validation_details: {},
  quality_indicators: [],
  quality_concerns: [],
  final_quality_score: 0
};

// 1. Family Structure Validation (25 points)
const familyAnalysis = data.context_analysis.family_analysis;
const dominantFamilyType = data.dominant_patterns.family_type;

if (dominantFamilyType === 'blended_family' && extractedData.children_count > 0) {
  validation.context_match_score += 25;
  validation.quality_indicators.push('Family structure consistent with blended family context');
} else if (dominantFamilyType === 'special_needs' && extractedData.children_count > 0) {
  validation.context_match_score += 25;
  validation.quality_indicators.push('Family structure consistent with special needs context');
} else if (dominantFamilyType === 'minor_children' && extractedData.children_count > 0) {
  validation.context_match_score += 20;
  validation.quality_indicators.push('Children count consistent with minor children indicators');
} else if (data.dominant_patterns.family_confidence < 0.1) {
  validation.context_match_score += 15; // Neutral score for unclear family context
  validation.quality_indicators.push('Family context unclear - neutral validation');
} else {
  validation.quality_concerns.push(`Family extraction may not match ${dominantFamilyType} context`);
}

// 2. Business Structure Validation (25 points)
const businessAnalysis = data.context_analysis.business_analysis;
const dominantBusinessType = data.dominant_patterns.business_type;

if (dominantBusinessType !== 'none' && extractedData.business_entities > 0) {
  validation.context_match_score += 25;
  validation.quality_indicators.push(`Business entities consistent with ${dominantBusinessType} indicators`);
} else if (dominantBusinessType === 'none' && extractedData.business_entities === 0) {
  validation.context_match_score += 20;
  validation.quality_indicators.push('No business entities - consistent with context');
} else if (data.dominant_patterns.business_confidence < 0.1) {
  validation.context_match_score += 15;
  validation.quality_indicators.push('Business context unclear - neutral validation');
} else {
  validation.quality_concerns.push(`Business entity count may not match ${dominantBusinessType} context`);
}

// 3. Urgency Level Validation (25 points)
const urgencyAnalysis = data.context_analysis.urgency_analysis;
const dominantUrgencyLevel = data.dominant_patterns.urgency_level;
const expectedUrgencyScore = data.dominant_patterns.urgency_metadata.urgency_score || 5;

const urgencyDiff = Math.abs(extractedData.urgency_score - expectedUrgencyScore);
if (urgencyDiff <= 1) {
  validation.context_match_score += 25;
  validation.quality_indicators.push(`Urgency score (${extractedData.urgency_score}) matches ${dominantUrgencyLevel} context`);
} else if (urgencyDiff <= 2) {
  validation.context_match_score += 15;
  validation.quality_indicators.push(`Urgency score close to ${dominantUrgencyLevel} expectation`);
} else {
  validation.quality_concerns.push(`Urgency score (${extractedData.urgency_score}) differs from ${dominantUrgencyLevel} context (expected ~${expectedUrgencyScore})`);
}

// 4. Meeting Outcome Validation (25 points)
const outcomeAnalysis = data.context_analysis.outcome_analysis;
const dominantOutcome = data.dominant_patterns.meeting_outcome;

const outcomeMap = {
  'closed_won': 'Closed Won',
  'follow_up': 'Follow Up',
  'closed_lost': 'Closed Lost'
};

if (outcomeMap[dominantOutcome] === extractedData.meeting_stage) {
  validation.context_match_score += 25;
  validation.quality_indicators.push(`Meeting outcome (${extractedData.meeting_stage}) matches context prediction`);
} else if (data.dominant_patterns.outcome_confidence < 0.2) {
  validation.context_match_score += 15;
  validation.quality_indicators.push('Meeting outcome context unclear - neutral validation');
} else {
  validation.quality_concerns.push(`Meeting outcome (${extractedData.meeting_stage}) differs from context prediction (${dominantOutcome})`);
}

// 5. Confidence Score Analysis
const confidenceScores = extractedData.confidence_scores || {};
const avgConfidence = Object.values(confidenceScores)
  .filter(score => typeof score === 'number' && score >= 1 && score <= 10)
  .reduce((sum, score, _, arr) => sum + score / arr.length, 0);

// Final Quality Score Calculation
validation.final_quality_score = Math.round(
  (validation.context_match_score * 0.7) + // Context matching (70%)
  (avgConfidence * 10 * 0.3) // Confidence scores (30%)
);

// Quality categorization
let qualityCategory;
if (validation.final_quality_score >= 85) {
  qualityCategory = 'Excellent';
} else if (validation.final_quality_score >= 70) {
  qualityCategory = 'Good';
} else if (validation.final_quality_score >= 55) {
  qualityCategory = 'Acceptable';
} else {
  qualityCategory = 'Poor';
}

// Store validation details
validation.validation_details = {
  family_context_score: Math.min(25, validation.context_match_score >= 25 ? 25 : validation.context_match_score >= 15 ? 15 : 0),
  business_context_score: Math.min(25, validation.context_match_score >= 50 ? 25 : validation.context_match_score >= 40 ? 15 : 0),
  urgency_context_score: Math.min(25, validation.context_match_score >= 75 ? 25 : validation.context_match_score >= 65 ? 15 : 0),
  outcome_context_score: Math.min(25, validation.context_match_score >= 100 ? 25 : validation.context_match_score >= 90 ? 15 : 0),
  average_confidence: avgConfidence,
  quality_category: qualityCategory
};

return {
  json: {
    ...data,
    extracted_data: extractedData,
    validation: validation,
    processing_metadata: {
      ...data.processing_metadata,
      extraction_method: 'local_mistral_7b',
      validation_version: '3.0_enhanced',
      context_cross_referenced: true,
      similar_cases_referenced: data.similar_cases_count || 0,
      final_processing_timestamp: new Date().toISOString()
    }
  }
};

// Enhanced Baserow formatting with validation metadata
const data = $input.first().json;
const extracted = data.extracted_data;
const validation = data.validation;

// Baserow field mappings
const fieldMappings = {
  lead_name: 6755,
  meeting_stage: 6756,
  marital_status: 6757,
  children_count: 6758,
  estate_value: 6759,
  real_estate_count: 6760,
  llc_interest: 6761,
  pain_points: 6762,
  objections: 6763,
  urgency_score: 6764,
  follow_up_required: 6765,
  transcript_file: 6766,
  processed_date: 6767,
  state: 6768
};

// Enhanced option ID mappings
const maritalStatusMap = {
  'Single': 2988,
  'Married': 2989,
  'Widowed': 2990,
  'Divorced': 2991
};

const meetingStageMap = {
  'Closed Won': 2984,
  'Closed Lost': 2985,
  'No Show': 2986,
  'Follow Up': 2987
};

// Create enhanced Baserow payload
const baserowPayload = {
  [`field_${fieldMappings.lead_name}`]: extracted.client_name,
  [`field_${fieldMappings.meeting_stage}`]: meetingStageMap[extracted.meeting_stage] || 2987,
  [`field_${fieldMappings.marital_status}`]: maritalStatusMap[extracted.marital_status] || null,
  [`field_${fieldMappings.children_count}`]: Math.max(0, parseInt(extracted.children_count) || 0),
  [`field_${fieldMappings.estate_value}`]: Math.max(0, parseInt(extracted.estate_value) || 0),
  [`field_${fieldMappings.real_estate_count}`]: 1, // Default assumption
  [`field_${fieldMappings.llc_interest}`]: Math.max(0, parseInt(extracted.business_entities) || 0),
  [`field_${fieldMappings.pain_points}`]: (extracted.pain_points || 'Estate planning consultation').substring(0, 2000),
  [`field_${fieldMappings.objections}`]: (extracted.objections || '').substring(0, 2000),
  [`field_${fieldMappings.urgency_score}`]: Math.min(10, Math.max(1, parseInt(extracted.urgency_score) || 5)),
  [`field_${fieldMappings.follow_up_required}`]: extracted.meeting_stage !== 'Closed Won',
  [`field_${fieldMappings.transcript_file}`]: `${extracted.client_name}: Estate Planning Advisor Meeting.txt`,
  [`field_${fieldMappings.processed_date}`]: new Date().toISOString().split('T')[0]
  // Note: State mapping would need full state option IDs from current_fields.json
};

// Enhanced quality metadata
const enhancedQualityMetadata = {
  // Quality Scores
  context_match_score: validation.context_match_score,
  final_quality_score: validation.final_quality_score,
  quality_category: validation.validation_details.quality_category,

  // Context Analysis Results
  family_context: {
    type: data.dominant_patterns.family_type,
    confidence: data.dominant_patterns.family_confidence,
    planning_impact: data.dominant_patterns.family_metadata.planning_impact
  },
  business_context: {
    type: data.dominant_patterns.business_type,
    confidence: data.dominant_patterns.business_confidence,
    complexity_score: data.dominant_patterns.business_metadata.complexity_score
  },
  urgency_context: {
    level: data.dominant_patterns.urgency_level,
    confidence: data.dominant_patterns.urgency_confidence,
    timeframe: data.dominant_patterns.urgency_metadata.timeframe
  },

  // Validation Results
  validation_flags: validation.quality_indicators,
  quality_concerns: validation.quality_concerns,
  confidence_scores: extracted.confidence_scores,

  // Processing Metadata
  vector_enhanced: true,
  local_processing: true,
  similar_cases_used: data.similar_cases_count || 0,
  knowledge_graph_version: data.processing_metadata.knowledge_base_version,
  extraction_method: 'local_mistral_7b_with_context',
  processing_timestamp: data.processing_metadata.final_processing_timestamp,

  // Extraction Notes
  extraction_notes: extracted.extraction_notes || 'No additional notes',

  // Auto-approve threshold
  auto_approved: validation.final_quality_score >= 70,
  requires_review: validation.final_quality_score < 70
};

return {
  json: {
    baserow_payload: baserowPayload,
    quality_metadata: enhancedQualityMetadata,
    processing_summary: {
      client_name: extracted.client_name,
      quality_score: validation.final_quality_score,
      quality_category: validation.validation_details.quality_category,
      context_enhanced: true,
      vector_processed: true,
      local_model_used: 'mistral:7b',
      auto_approved: validation.final_quality_score >= 70
    },
    original_data: data
  }
};

// Log processing completion with detailed metrics
const data = $input.first().json;
const summary = data.processing_summary || data.quality_metadata;

console.log('üéâ VECTOR-ENHANCED PROCESSING COMPLETED');
console.log('=' * 50);
console.log(`üìã Client: ${summary.client_name}`);
console.log(`üìä Quality Score: ${summary.quality_score}/100 (${summary.quality_category})`);
console.log(`ü§ñ Model: ${summary.local_model_used || 'mistral:7b'}`);
console.log(`‚úÖ Auto-approved: ${summary.auto_approved ? 'Yes' : 'No'}`);
console.log(`üîç Context Enhanced: ${summary.context_enhanced ? 'Yes' : 'No'}`);
console.log(`üìä Vector Processed: ${summary.vector_processed ? 'Yes' : 'No'}`);

