// Split the file list into individual items
const fileList = items[0].json.stdout;
const files = fileList.split('\n').filter(f => f.trim().length > 0);

const results = files.map((filePath, index) => ({
  json: {
    filePath: filePath.trim(),
    fileName: filePath.split('/').pop(),
    batchIndex: index + 1,
    totalFiles: files.length,
    processedAt: new Date().toISOString()
  }
}));

return results;

// Enhanced metadata extraction for batch processing
const content = items[0].json.transcriptContent;
const filePath = items[0].json.filePath;
const fileName = items[0].json.fileName;
const batchIndex = items[0].json.batchIndex;
const totalFiles = items[0].json.totalFiles;

// Parse filename for metadata (YYYY-MM-DD_CompanyName_MeetingType.txt)
const fileNameParts = fileName.split('_');
const dateMatch = fileName.match(/\d{4}-\d{2}-\d{2}/);

// Extract basic info from content
const lines = content.split('\n');
const wordCount = content.split(/\s+/).length;
const duration = content.match(/Duration.*?(\d+)\s*min/i)?.[1] || 'Unknown';

// Enhanced company name extraction
let companyName = 'Unknown';
if (fileNameParts.length > 1) {
  companyName = fileNameParts[1];
} else {
  // Try to extract from content
  const companyMatch = content.match(/(?:Company|Client|Organization):\s*([^\n]+)/i);
  if (companyMatch) companyName = companyMatch[1].trim();
}

return [{
  json: {
    fileName,
    filePath,
    transcriptContent: content,
    meetingDate: dateMatch ? dateMatch[0] : new Date().toISOString().split('T')[0],
    companyName: companyName.replace(/[^a-zA-Z0-9\s]/g, ''),
    meetingType: fileNameParts[2]?.replace('.txt', '').replace('.md', '') || 'Meeting',
    wordCount,
    estimatedDuration: duration,
    batchInfo: {
      currentFile: batchIndex,
      totalFiles: totalFiles,
      progress: Math.round((batchIndex / totalFiles) * 100)
    },
    processedAt: new Date().toISOString(),
    source: 'batch_processing'
  }
}];

// BMad Method Analyst Agent for batch processing
const transcriptData = items[0].json;

// Enhanced prompt for batch processing with progress tracking
const analysisPrompt = `
I am Mary, the BMad Method Business Analyst, processing transcript ${transcriptData.batchInfo.currentFile} of ${transcriptData.batchInfo.totalFiles} (${transcriptData.batchInfo.progress}% complete).

Analyzing sales meeting transcript for comprehensive CRM data extraction:

Meeting Details:
- Company: ${transcriptData.companyName}
- Date: ${transcriptData.meetingDate}
- Type: ${transcriptData.meetingType}
- File: ${transcriptData.fileName}

Transcript Content:
${transcriptData.transcriptContent}

Please extract comprehensive sales intelligence in JSON format:

{
  "contactInfo": [
    {
      "name": "Contact name",
      "title": "Job title",
      "email": "email if mentioned",
      "phone": "phone if mentioned",
      "role": "decision_maker|influencer|user|gatekeeper"
    }
  ],
  "companyDetails": {
    "size": "startup|small|medium|large|enterprise",
    "industry": "Industry category",
    "revenue": "Revenue range if mentioned",
    "employees": "Employee count if mentioned",
    "headquarters": "Location if mentioned"
  },
  "salesStage": "lead_qualification|needs_assessment|solution_presentation|proposal|negotiation|closing|won|lost",
  "salesMetrics": {
    "dealSize": "Potential deal value",
    "probability": "Win probability percentage",
    "timeline": "Expected close timeline",
    "competitors": ["List of competitors mentioned"]
  },
  "painPoints": [
    {
      "description": "Specific pain point",
      "impact": "Business impact described",
      "urgency": "high|medium|low"
    }
  ],
  "requirements": [
    {
      "requirement": "Specific requirement",
      "priority": "must_have|nice_to_have|future"
    }
  ],
  "budgetIndicators": {
    "range": "Budget range if mentioned",
    "timeframe": "Budget availability timeline",
    "approvalProcess": "Who needs to approve"
  },
  "decisionCriteria": [
    {
      "criteria": "Decision factor",
      "weight": "high|medium|low"
    }
  ],
  "nextSteps": [
    {
      "action": "Specific next step",
      "owner": "Who is responsible",
      "timeline": "When it should happen"
    }
  ],
  "keyQuotes": [
    {
      "quote": "Exact quote from transcript",
      "speaker": "Who said it",
      "context": "Why this quote is significant"
    }
  ],
  "sentiment": {
    "overall": "positive|neutral|negative",
    "engagement": "high|medium|low",
    "buyingSignals": ["Positive buying signals detected"]
  }
}

Focus on extracting actionable sales intelligence for CRM population and dashboard insights.
`;

return [{
  json: {
    ...transcriptData,
    analysisPrompt,
    status: 'ready_for_analysis',
    processingMode: 'batch'
  }
}];

// Parse LLM response and validate for batch processing
const originalData = items[0].json;
const llmResponse = originalData.message?.content || originalData.text;

try {
  // Extract JSON from LLM response
  const jsonMatch = llmResponse.match(/\{[\s\S]*\}/);
  const salesData = jsonMatch ? JSON.parse(jsonMatch[0]) : {};

  // Validate required fields
  const requiredFields = ['contactInfo', 'companyDetails', 'salesStage', 'painPoints', 'nextSteps'];
  const missingFields = requiredFields.filter(field => !salesData.hasOwnProperty(field));

  if (missingFields.length > 0) {
    throw new Error(`Missing required fields: ${missingFields.join(', ')}`);
  }

  // Enhanced data for Notion CRM
  return [{
    json: {
      // Original metadata
      fileName: originalData.fileName,
      filePath: originalData.filePath,
      meetingDate: originalData.meetingDate,
      companyName: originalData.companyName,
      meetingType: originalData.meetingType,
      source: originalData.source,
      batchInfo: originalData.batchInfo,

      // Extracted sales data
      contactInfo: salesData.contactInfo || [],
      companyDetails: salesData.companyDetails || {},
      salesStage: salesData.salesStage || 'Unknown',
      salesMetrics: salesData.salesMetrics || {},
      nextSteps: salesData.nextSteps || [],
      painPoints: salesData.painPoints || [],
      requirements: salesData.requirements || [],
      budgetIndicators: salesData.budgetIndicators || {},
      decisionCriteria: salesData.decisionCriteria || [],
      keyQuotes: salesData.keyQuotes || [],
      sentiment: salesData.sentiment || {},

      // Processing metadata
      extractionStatus: 'completed',
      extractedAt: new Date().toISOString(),
      rawLLMResponse: llmResponse,

      // CRM ready fields
      primaryContact: salesData.contactInfo?.[0]?.name || 'Unknown',
      primaryEmail: salesData.contactInfo?.find(c => c.email)?.email || '',
      dealValue: salesData.salesMetrics?.dealSize || 'TBD',
      closeTimeline: salesData.salesMetrics?.timeline || 'Unknown',
      urgencyLevel: salesData.painPoints?.find(p => p.urgency === 'high') ? 'High' : 
                   salesData.painPoints?.find(p => p.urgency === 'medium') ? 'Medium' : 'Low'
    }
  }];
} catch (error) {
  console.error('Batch processing error for', originalData.fileName, ':', error.message);
  return [{
    json: {
      ...originalData,
      extractionStatus: 'failed',
      error: error.message,
      rawLLMResponse: llmResponse,
      retryNeeded: true
    }
  }];
}

// Log batch processing progress
const data = items[0].json;

console.log(`‚úÖ Processed ${data.batchInfo.currentFile}/${data.batchInfo.totalFiles}: ${data.companyName} - ${data.fileName}`);
console.log(`üìä Progress: ${data.batchInfo.progress}% complete`);

return [{
  json: {
    ...data,
    progressLogged: true,
    notionRecordCreated: true
  }
}];

// Log failed processing for review
const data = items[0].json;

console.error(`‚ùå Failed to process ${data.batchInfo.currentFile}/${data.batchInfo.totalFiles}: ${data.fileName}`);
console.error(`Error: ${data.error}`);

// Could implement retry logic here or save to separate failure tracking
return [{
  json: {
    ...data,
    failureLogged: true,
    requiresManualReview: true
  }
}];

// Generate batch processing summary
const allItems = items;
const totalProcessed = allItems.length;
const successful = allItems.filter(item => item.json.extractionStatus === 'completed').length;
const failed = allItems.filter(item => item.json.extractionStatus === 'failed').length;

const summary = {
  batchCompleted: new Date().toISOString(),
  totalFiles: totalProcessed,
  successful: successful,
  failed: failed,
  successRate: Math.round((successful / totalProcessed) * 100),
  companiesProcessed: [...new Set(allItems.map(item => item.json.companyName))],
  salesStages: allItems.reduce((acc, item) => {
    const stage = item.json.salesStage;
    acc[stage] = (acc[stage] || 0) + 1;
    return acc;
  }, {})
};

console.log('üéâ Batch Processing Complete!');
console.log(`üìà Results: ${successful}/${totalProcessed} files processed successfully (${summary.successRate}%)`);
console.log(`üè¢ Companies: ${summary.companiesProcessed.length} unique companies`);
console.log(`üìä Sales Stages:`, summary.salesStages);

return [{
  json: summary
}];
