// Split the file list into individual items
const fileList = items[0].json.stdout;
const files = fileList.split('\n').filter(f => f.trim().length > 0);

const results = files.map((filePath, index) => {
  const fileName = filePath.split('/').pop();

  // Extract client name from filename
  let clientName = 'Unknown';
  const nameMatch = fileName.match(/^([^:]+):/);  
  if (nameMatch) {
    clientName = nameMatch[1].trim();
  }

  return {
    json: {
      filePath: filePath.trim(),
      fileName: fileName,
      clientName: clientName,
      deal_id: index + 3900, // Starting from where your DB leaves off
      batchIndex: index + 1,
      totalFiles: files.length,
      processedAt: new Date().toISOString()
    }
  };
});

return results;

// Prepare comprehensive estate planning data extraction
const content = items[0].json.transcriptContent;
const clientName = items[0].json.clientName;
const fileName = items[0].json.fileName;
const deal_id = items[0].json.deal_id;

// Extract meeting date if present in filename or content
const dateMatch = fileName.match(/(?:May|June|July|August|September|October|November|December)\s+\d{1,2}/i);
const meetingDate = dateMatch ? `2025/${dateMatch[0]}` : '2025/09/19';

// Create comprehensive extraction prompt
const analysisPrompt = `
You are an expert estate planning analyst. Extract comprehensive information from this estate planning advisor meeting transcript.

Client: ${clientName}
File: ${fileName}

Transcript Content:
${content}

Extract ALL of the following information in JSON format. If information is not explicitly stated, mark as null.

{
  "client_info": {
    "name": "${clientName}",
    "age": "Extract age if mentioned",
    "state_of_residence": "Extract state (use 2-letter code)",
    "marital_status": "Single|Married|Divorced|Widowed",
    "spouse_name": "Name if married",
    "phone": "Phone number if mentioned",
    "email": "Email if mentioned",
    "occupation": "Their profession or retired"
  },
  "meeting_details": {
    "stage": "Follow up|Closed Won|Closed Lost|No Show",
    "objection": "Any concerns or objections raised",
    "reason": "Reason for their interest or decision",
    "demo_requested": "Yes if they want to see more, No otherwise"
  },
  "estate_details": {
    "estate_value": "Total estate value in dollars (number only)",
    "payment": "Amount they're paying for services (number only)",
    "deposit": "Deposit amount if mentioned (number only)",
    "estate_complexity": "Simple|Moderate|Complex based on assets"
  },
  "beneficiaries": {
    "number_of_beneficiaries": "Total count",
    "primary_beneficiaries": ["List names and relationships"],
    "contingent_beneficiaries": ["List if mentioned"],
    "charity_included": "Yes|No",
    "charity_names": ["List if any"]
  },
  "assets": {
    "real_estate": {
      "has_real_estate": "Yes|No",
      "number_of_properties": "Count",
      "primary_residence_state": "State",
      "other_properties_locations": ["List of states/locations"],
      "total_real_estate_value": "Estimated value"
    },
    "financial_assets": {
      "retirement_accounts": "Yes|No with types (401k, IRA, etc)",
      "investment_accounts": "Yes|No with details",
      "bank_accounts": "Mentioned or not",
      "life_insurance": "Yes|No with amount if mentioned",
      "business_ownership": "Yes|No with details"
    },
    "personal_property": {
      "vehicles": "Mentioned or not",
      "valuable_collections": "Art, jewelry, etc if mentioned",
      "other_significant_assets": "List any"
    }
  },
  "planning_needs": {
    "needs_will": "Yes|No|Already Has",
    "needs_trust": "Yes|No|Already Has with type (Living, Revocable, etc)",
    "needs_power_of_attorney": "Yes|No|Already Has",
    "needs_healthcare_directive": "Yes|No|Already Has",
    "llc_formation_interest": "Yes|No",
    "entity_type_needed": "LLC|Corporation|Partnership|None",
    "tax_planning_needed": "Yes|No with concerns",
    "special_needs_planning": "Yes|No with details",
    "pet_trust_needed": "Yes|No"
  },
  "family_dynamics": {
    "children_count": "Number of children",
    "children_ages": ["List ages or age ranges"],
    "minor_children": "Yes|No",
    "adult_children": "Yes|No",
    "blended_family": "Yes|No",
    "family_conflicts": "Any mentioned concerns",
    "special_circumstances": "Disabled beneficiary, addiction issues, etc"
  },
  "urgency_timeline": {
    "urgency_level": "High|Medium|Low",
    "timeline_driver": "Health, travel, age, tax deadline, etc",
    "desired_completion": "Timeframe mentioned",
    "follow_up_scheduled": "Date if mentioned"
  },
  "advisor_notes": {
    "key_concerns": ["List main concerns discussed"],
    "action_items": ["Next steps discussed"],
    "special_instructions": "Any specific requests",
    "referral_source": "How they found the service",
    "additional_services_discussed": ["Other services mentioned"]
  },
  "extracted_quotes": [
    "Important quotes that show motivation or concerns"
  ]
}

Be thorough and extract as much information as possible from the transcript.
`;

return [{
  json: {
    ...items[0].json,
    analysisPrompt,
    meetingDate,
    status: 'ready_for_analysis'
  }
}];

// Parse LLM response and structure for Notion
const originalData = items[0].json;
const llmResponse = originalData.message?.content || originalData.text;

try {
  // Extract JSON from LLM response
  const jsonMatch = llmResponse.match(/\{[\s\S]*\}/);
  if (!jsonMatch) throw new Error('No JSON found in response');

  const extractedData = JSON.parse(jsonMatch[0]);

  // Build comprehensive Notion record
  const notionRecord = {
    // Core identifiers
    deal_id: originalData.deal_id,
    date: originalData.meetingDate,

    // Client Information (matching your existing columns)
    lead: extractedData.client_info?.name || originalData.clientName,
    stage: extractedData.meeting_details?.stage || 'Follow up',
    demo_requested: extractedData.meeting_details?.demo_requested || '',
    objection: extractedData.meeting_details?.objection || '',
    reason: extractedData.meeting_details?.reason || '',

    // Financial columns
    payment: parseFloat(extractedData.estate_details?.payment) || 0,
    deposit: parseFloat(extractedData.estate_details?.deposit) || 0,

    // New Estate Planning Specific Columns
    state_of_residence: extractedData.client_info?.state_of_residence || '',
    marital_status: extractedData.client_info?.marital_status || '',
    age: extractedData.client_info?.age || '',
    spouse_name: extractedData.client_info?.spouse_name || '',

    // Beneficiary Information
    number_of_beneficiaries: extractedData.beneficiaries?.number_of_beneficiaries || 0,
    primary_beneficiaries: (extractedData.beneficiaries?.primary_beneficiaries || []).join(', '),
    contingent_beneficiaries: (extractedData.beneficiaries?.contingent_beneficiaries || []).join(', '),

    // Estate Information
    estate_value: extractedData.estate_details?.estate_value || '',
    estate_complexity: extractedData.estate_details?.estate_complexity || '',

    // Real Estate
    has_real_estate: extractedData.assets?.real_estate?.has_real_estate || 'No',
    number_of_properties: extractedData.assets?.real_estate?.number_of_properties || 0,
    property_locations: (extractedData.assets?.real_estate?.other_properties_locations || []).join(', '),

    // Entity Formation
    llc_formation_interest: extractedData.planning_needs?.llc_formation_interest || 'No',
    entity_type_needed: extractedData.planning_needs?.entity_type_needed || 'None',

    // Planning Documents Needed
    needs_will: extractedData.planning_needs?.needs_will || '',
    needs_trust: extractedData.planning_needs?.needs_trust || '',
    trust_type: extractedData.planning_needs?.needs_trust?.includes('Yes') ? 
                 extractedData.planning_needs?.needs_trust : '',
    needs_poa: extractedData.planning_needs?.needs_power_of_attorney || '',
    needs_healthcare_directive: extractedData.planning_needs?.needs_healthcare_directive || '',

    // Family Information
    children_count: extractedData.family_dynamics?.children_count || 0,
    minor_children: extractedData.family_dynamics?.minor_children || 'No',

    // Additional Assets
    retirement_accounts: extractedData.assets?.financial_assets?.retirement_accounts || '',
    life_insurance: extractedData.assets?.financial_assets?.life_insurance || '',
    business_ownership: extractedData.assets?.financial_assets?.business_ownership || '',

    // Urgency and Timeline
    urgency_level: extractedData.urgency_timeline?.urgency_level || 'Medium',
    timeline_driver: extractedData.urgency_timeline?.timeline_driver || '',
    desired_completion: extractedData.urgency_timeline?.desired_completion || '',

    // Comprehensive Notes Section
    notes: `**ESTATE PLANNING ANALYSIS**\n\n` +
           `**Client Information:**\n` +
           `- Name: ${extractedData.client_info?.name}\n` +
           `- Age: ${extractedData.client_info?.age || 'Not specified'}\n` +
           `- State: ${extractedData.client_info?.state_of_residence || 'Not specified'}\n` +
           `- Marital Status: ${extractedData.client_info?.marital_status || 'Not specified'}\n` +
           `- Spouse: ${extractedData.client_info?.spouse_name || 'N/A'}\n\n` +

           `**Estate Overview:**\n` +
           `- Total Estate Value: ${extractedData.estate_details?.estate_value || 'Not disclosed'}\n` +
           `- Complexity: ${extractedData.estate_details?.estate_complexity || 'Not assessed'}\n\n` +

           `**Beneficiaries (${extractedData.beneficiaries?.number_of_beneficiaries || 0} total):**\n` +
           `- Primary: ${(extractedData.beneficiaries?.primary_beneficiaries || []).join(', ') || 'Not specified'}\n` +
           `- Contingent: ${(extractedData.beneficiaries?.contingent_beneficiaries || []).join(', ') || 'None'}\n\n` +

           `**Real Estate Holdings:**\n` +
           `- Has Real Estate: ${extractedData.assets?.real_estate?.has_real_estate}\n` +
           `- Number of Properties: ${extractedData.assets?.real_estate?.number_of_properties}\n` +
           `- Locations: ${(extractedData.assets?.real_estate?.other_properties_locations || []).join(', ') || 'Not specified'}\n\n` +

           `**Financial Assets:**\n` +
           `- Retirement Accounts: ${extractedData.assets?.financial_assets?.retirement_accounts || 'Not mentioned'}\n` +
           `- Investment Accounts: ${extractedData.assets?.financial_assets?.investment_accounts || 'Not mentioned'}\n` +
           `- Life Insurance: ${extractedData.assets?.financial_assets?.life_insurance || 'Not mentioned'}\n` +
           `- Business Ownership: ${extractedData.assets?.financial_assets?.business_ownership || 'No'}\n\n` +

           `**Planning Needs:**\n` +
           `- Will: ${extractedData.planning_needs?.needs_will}\n` +
           `- Trust: ${extractedData.planning_needs?.needs_trust}\n` +
           `- Power of Attorney: ${extractedData.planning_needs?.needs_power_of_attorney}\n` +
           `- Healthcare Directive: ${extractedData.planning_needs?.needs_healthcare_directive}\n` +
           `- LLC/Entity Formation: ${extractedData.planning_needs?.llc_formation_interest}\n` +
           `- Tax Planning: ${extractedData.planning_needs?.tax_planning_needed || 'Not discussed'}\n\n` +

           `**Family Dynamics:**\n` +
           `- Children: ${extractedData.family_dynamics?.children_count || 0}\n` +
           `- Minor Children: ${extractedData.family_dynamics?.minor_children}\n` +
           `- Special Circumstances: ${extractedData.family_dynamics?.special_circumstances || 'None noted'}\n\n` +

           `**Key Concerns:**\n` +
           (extractedData.advisor_notes?.key_concerns || []).map(c => `- ${c}`).join('\n') + '\n\n' +

           `**Action Items:**\n` +
           (extractedData.advisor_notes?.action_items || []).map(a => `- ${a}`).join('\n') + '\n\n' +

           `**Notable Quotes:**\n` +
           (extractedData.extracted_quotes || []).map(q => `"${q}"`).join('\n') + '\n\n' +

           `**Source:** ${originalData.fileName}\n` +
           `**Processed:** ${new Date().toISOString()}`,

    // Metadata
    source_file: originalData.fileName,
    batch_processed: true,
    extraction_status: 'completed',
    processed_at: new Date().toISOString(),

    // Store full extracted data for reference
    _fullExtractedData: extractedData
  };

  return [{
    json: notionRecord
  }];

} catch (error) {
  console.error('Error parsing data for', originalData.fileName, ':', error.message);
  return [{
    json: {
      deal_id: originalData.deal_id,
      lead: originalData.clientName,
      stage: 'No Show',
      notes: `ERROR: Failed to process transcript. ${error.message}`,
      source_file: originalData.fileName,
      extraction_status: 'failed',
      error: error.message
    }
  }];
}

// Log progress
const data = items[0].json;
console.log(`âœ… Processed: ${data.lead} - ${data.source_file}`);
console.log(`   Estate Value: ${data.estate_value || 'Not disclosed'}`);
console.log(`   Beneficiaries: ${data.number_of_beneficiaries}`);
console.log(`   Stage: ${data.stage}`);

return items;
